import React from 'react';
import style from 'styled-components';
import MovieCard from '../components/MovieCard';

const SearchWrapper = style.div`

    margin-top: 23px;
    display: flex;
    justify-content: space-around;
    flex-wrap: wrap;


`;

const BtnWrapper = style.div`

    display: flex;
    justify-content: center;
    flex-wrap: nowrap;
    margin-top: 35px;


`

const PageBtn = style.button`

    background-color: transparent;
    border-color: transparent;
    background-blend-mode: multiply;
    background-repeat: no-repeat;
    border-radius: 16px;

`;

const PageIcon = style.img`

    width: 30px;
    height: 30px;
    margin: 0;

`;

const ErrorLabel = style.p`


    margin: 0;
    text-align: center;
    color: white;
    font-size: 24px;


`;

const PageNum = style.p`


    margin: 0;
    font-weight: bold;
    text-align: center;
    color: white;
    font-size: 24px;


`;

const Note = style.p`

    font-size: 16px;
    color: white;
    font-weight: 400;
    margin: 0;
    margin-top: 21px;
`;



export default function SearchList(
    { 
    movies, 
    addMovie, 
    nominates, 
    pages, 
    goNextPage, 
    goBackPage, 
    currPage, 
    input, 
    limit,
    date }) {

    const [leftIcon1, setLeftIcon1] = React.useState(<PageIcon src="/images/leftBtn.png"/>);
    const [leftIcon2, setLeftIcon2] = React.useState(<PageIcon src="/images/leftBtn.png"/>);
    const [rightIcon1, setRightIcon1] = React.useState(<PageIcon src="/images/rightBtn.png"/>);
    const [rightIcon2, setRightIcon2] = React.useState(<PageIcon src="/images/rightBtn.png"/>);


    const onClickLeft = () => {
        goBackPage();

    }

    const onClickRight = () => {
        goNextPage();
    }

    return (
    <>
    <Note>* If a movie doesn't show up, try refining the search by entering the year!</Note>
        {
                    pages === 1 || pages === 0 ? null : 
                    <BtnWrapper>
                        {
                            currPage === 1 ? null :
                            <PageBtn onClick={onClickLeft}>
                                <PageIcon src="/images/leftBtn.png"/>
                            </PageBtn>
                        }
                        { pages === -1 || input.length === 0 ? null : <PageNum>{currPage}</PageNum> }
                        {
                            currPage === pages || currPage === 100 || pages === -1 || input.length === 0 ? null:
                            <PageBtn onClick={onClickRight}>
                                <PageIcon src="/images/rightBtn.png"/>
                            </PageBtn>
                        }
                    </BtnWrapper>
        }

        <SearchWrapper>
                {
                    pages === -1 && input.length !== 0 ? 
                    <ErrorLabel>
                    { date.length === 0 ? `Results for "${input}" not found ðŸ˜¢` 
                    : `Results for "${input}" (${date}) not found ðŸ˜¢` }
                    </ErrorLabel> :
                    movies.map((movie) => {

                        let isNominated = false;

                        for(const nominate in nominates) {
                            if (nominates[nominate].imdbID === movie.imdbID) {
                                isNominated = true
                            } 
                        }
                        return <MovieCard limit={limit} btnLabel={!limit ? "Nominate": "List Filled"} selected={isNominated} addMovie={addMovie} movie={movie}/>
                    })
                }

        </SearchWrapper>
        {
                    pages === 1 || pages === 0 ? null : 
                    <BtnWrapper>
                        {
                            currPage === 1 ? null :
                            <PageBtn onClick={onClickLeft}>
                                <PageIcon src="/images/leftBtn.png"/>
                            </PageBtn>
                        }
                        { pages === -1 || input.length === 0 ? null : <PageNum>{currPage}</PageNum> }
                        {
                            currPage === pages || currPage === 100 || pages === -1 || input.length === 0 ? null:
                            <PageBtn onClick={onClickRight}>
                                <PageIcon src="/images/rightBtn.png"/>
                            </PageBtn>
                        }
                    </BtnWrapper>
        }
    </>
    )
}
