import React from 'react'
import style from 'styled-components';

const Row = style.div`

    display: flex;

`;


const CardWrapper = style.div`

  background-color: #353535;
  width: 340px;
  margin: 0px;
  border-radius: 12px;


`;

const MoviePoster = style.img`

    height: 327px;
    width: 201.57px;
    border-radius: 12px 0 0 12px;

`

const MovieTitle = style.h1`

  color: white;
  font-weight: bold;
  text-align: left;
  font-size: 24px;


`;

const MovieInfo = style.div`

    display: block;
    margin: 12px 0 0 12px;

`;

export default function MovieCard() {
    return (
        <div>
            <CardWrapper>
                <Row>
                    <MoviePoster src="/images/panther.jpg" alt="Movie_Poster"/>
                    <MovieInfo>
                        <MovieTitle>
                            Black Panther
                        </MovieTitle>

                    </MovieInfo>
                </Row>
            </CardWrapper>
        </div>
    )
}
