import React from 'react'
import style from 'styled-components';

const Row = style.div`

    display: flex;

`;


const CardWrapper = style.div`

  background-color: #353535;
  position: relative;
  display: inline-block;
  width: 363px;
  margin: 0px;
  border-radius: 12px;
  margin-top: 15px;



`;

const MoviePoster = style.img`

    height: 210px;
    width: 129px;
    border-radius: 12px 0 0 12px;

`

const MovieTitle = style.h1`

  color: white;
  margin: 0;
  font-weight: bold;
  text-align: left;
  font-size: 24px;


`;

const MovieDate = style.h1`

  color: white;
  margin: 0;
  font-weight: 500;
  text-align: left;
  font-size: 16px;


`;

const NominateBtn = style.button`

    border: 0;
    color: #FFE79B;
    font-size: 24px;
    font-weight: bold;
    margin: 0;
    padding: 0;
    position: absolute;
    right: 16px;
    bottom: 13px;
    background-color: transparent;

    &:disabled {
        color: #787363;
    }

`;

const MovieInfo = style.div`

    display: block;
    margin: 12px 0 0 12px;

`;

export default function MovieCard({ movie, addMovie }) {

    const [isNominated, setNominate] = React.useState(false)

    return (
            <CardWrapper>
                <Row>
                    <MoviePoster src={movie.Poster === "N/A" ? "/images/404poster.jpeg" : movie.Poster } alt="Movie_Poster"/>
                    <MovieInfo>
                        <MovieTitle>
                            {movie.Title}
                        </MovieTitle>
                        <MovieDate>
                            {movie.Year}
                        </MovieDate>
                        <NominateBtn onClick={() => { addMovie(movie) }} disabled={isNominated}>
                            Nominate
                        </NominateBtn>
                    </MovieInfo>
                </Row>
            </CardWrapper>
    )
}
