import React from 'react';
import style from 'styled-components';
import MovieCard from '../components/MovieCard';

const SearchWrapper = style.div`

    margin-top: 33px;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;


`;

const BtnWrapper = style.div`

    display: flex;
    justify-content: space-between;
    flex-wrap: nowrap;


`

const PageBtn = style.button`

    background-color: transparent;
    border-color: transparent;
    background-repeat: no-repeat;
    border-radius: 16px;

`;

const PageIcon = style.img`

    width: 30px;
    height: 30px;
    margin: 0;



`;


export default function SearchList({ movies, addMovie, nominates, pages }) {


    return (
    <>
        <SearchWrapper>
                {
                    movies.map((movie) => {

                        let isNominated = false;

                        for(const nominate in nominates) {
                            if (nominates[nominate].imdbID === movie.imdbID) {
                                isNominated = true
                            } 
                        }
                        return <MovieCard btnLabel={"Nominate"} selected={isNominated} addMovie={addMovie} movie={movie}/>
                    })
                }
                {
                    movies === null ? null : 
                    <BtnWrapper>
                        <PageBtn>
                            <PageIcon src="/images/leftBtn.png"/>
                        </PageBtn>
                        <PageBtn>
                            <PageIcon src="/images/rightBtn.png"/>
                        </PageBtn>
                    </BtnWrapper>
                }

        </SearchWrapper>
        </>
    )
}
