import React from 'react';
import style from 'styled-components';
import MovieCard from '../components/MovieCard';

const SearchWrapper = style.div`

    margin-top: 33px;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;


`;

const BtnWrapper = style.div`

    display: flex;
    flex-wrap: nowrap;
    margin-top: 35px;


`

const PageBtn = style.button`

    background-color: transparent;
    border-color: transparent;
    background-repeat: no-repeat;
    border-radius: 16px;

`;

const PageIcon = style.img`

    width: 30px;
    height: 30px;
    margin: 0;



`;


export default function SearchList({ movies, addMovie, nominates, pages, goNextPage, goBackPage, currPage}) {

    console.log(currPage);
    console.log(pages);
    const onClickLeft = () => {
        goBackPage();
    }

    const onClickRight = () => {
        goNextPage();
    }

    return (
    <>
        <SearchWrapper>
                {
                    movies.map((movie) => {

                        let isNominated = false;

                        for(const nominate in nominates) {
                            if (nominates[nominate].imdbID === movie.imdbID) {
                                isNominated = true
                            } 
                        }
                        return <MovieCard btnLabel={"Nominate"} selected={isNominated} addMovie={addMovie} movie={movie}/>
                    })
                }

        </SearchWrapper>
    </>
    )
}
