import React from 'react'
import SearchList from '../components/SearchList';
import config from '../config.json';
import parseMovie from '../util';
import style from 'styled-components';

const request = require('request');

const NominateTitle = style.h1`

  color: white;
  font-weight: bold;
  text-align: left;
  font-size: 48px;
  margin-bottom: 73px;

`;

const Form = style.input`

    width: 100%;
    height: 31px;
    color: white;
    font-size: 18px;
    font-weight: 400;
    background-color: transparent;
    border-style: solid;
    border-top: 0;
    border-left: 0;
    border-right: 0;
    border-bottom: 5px solid white;

    &::placeholder {
        font-size: 18px;
        color: #E3E3E3;
        font-weight: 400;
    }

`;

const Wrapper = style.div`

  margin: 10.21% 22% 5.07% 22%;

`;


export default function Nominate() {

    const [searchInput, setInput] = React.useState("");
    const [searchOutput, setOuput] = React.useState([]);
    const [pageCount, setPages] = React.useState(0);

    React.useEffect(() => {
        request(`http://www.omdbapi.com/?s=${searchInput}&apikey=${config.key}&page=1&type=movie&r=json`, { json: true }, (err, res, body) =>  {
            if (body.Response === "False") {
                setOuput([]);
                console.log(body.Error);
                return
            }

            const movieCount = body.totalResults;
            const currMovies = body.Search;

            setPages(Number(movieCount/10) + 1)
            setOuput(currMovies)


        });
    }, [searchInput]);

    const handleChange =(e) => {
        setInput(e.target.value);
    }
    return (
        <Wrapper>
            <NominateTitle>
                Start Nominating
            </NominateTitle>
            <Form placeholder="Search for film title (or not up to you)" onChange={handleChange}/>
            <SearchList movies={searchOutput}/>
        </Wrapper>
    );
}
