import React from 'react';
import style from 'styled-components';
import Landing from './page/Landing';
import Nominate from './page/Nominate';
import UserList from './page/UserList';

const Wrapper = style.div`

  background-color: #232323;

`;


function App() {

  const [nominatedMovies, nominateMovie] = React.useState([]);
  const [isLimit, setIsLimit] = React.useState(false);

  React.useEffect(() => {
    if (nominatedMovies.length === 5) {
        openBanner();
        setIsLimit(true);
    }
  }, [nominatedMovies]);

  const addMovie = (movie) => {

    nominateMovie((prevState) => {

      const copy = JSON.parse(JSON.stringify(prevState));
      copy.push(movie);

      if (copy.length === 5) {
        setIsLimit(true)
      }

      return copy

    });
  }

  const removeMovie = (movie) => {
    nominateMovie((prevState) => {

      let copy = JSON.parse(JSON.stringify(prevState));
      copy = copy.filter((movieCopy) => {
        return movie.imdbID !== movieCopy.imdbID
      });

      return copy

    });
  }

  const openBanner = () => {
    var x = document.getElementById("snackbar");
    x.className = "show";
    setTimeout(function(){ x.className = x.className.replace("show", ""); }, 3000);
  }



  return (
      <Wrapper>
        <Landing/>
        <Nominate addMovie={addMovie} movies={nominatedMovies}/>
        <UserList movies={nominatedMovies} removeMovie={removeMovie}/>
        <div id="snackbar">Some text some message..</div>
      </Wrapper>
  );
}

export default App;
