import React from 'react';
import style from 'styled-components';
import MovieCard from '../components/MovieCard';

const SearchWrapper = style.div`

    margin-top: 33px;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;


`;


export default function SearchList({ movies, addMovie, nominates }) {

    React.useEffect(() => {

    }, [nominates])

    return (
        <SearchWrapper>
                {
                    movies.map((movie) => {

                        let isNominated = false;
                            con
                        for(const nominate in nominates) {
                            if (nominate.imdbID === movie.imdbID) {
                                isNominated = true
                            } 
                        }
                        return <MovieCard btnLabel={"Nominate"} selected={isNominated} addMovie={addMovie} movie={movie}/>
                    })
                }
        </SearchWrapper>
    )
}
